  //     .then(arr => {
  //       console.log(arr);
  //       return arr;
  //     })
  //     .catch(error => console.log(error))
  //   return games;
  // }
  // console.log(getgames())
  const [showMsg, setShowMsg] = useState(true);
  const [searching, setSearching] = useState(false);


  useEffect(()=>{
    if(origin==='api'){
      dispatch(getApiGames());
      setShowMsg(false);
      setSearching(true);
    }
    if(origin==='db'){
      console.log('falta cargar videojuegos en la base de datos');
      // dispatch(getdbGames());
      setShowMsg(false);
      setSearching(true);
    }
  },[origin,dispatch])

  useEffect(()=>{
  },[showMsg,searching])

  // traerme el arreglo
  // traerme el género y el título a buscar
  // hacer el filtrado
  // mostrar de a 6
  // 

  // si es api y el texto de búsqueda es '' trae 100 de la ruta local / videogames


  return (
    <div>
      <div className="p_cards_area">
        {showMsg && <h3> Seleccione primero el orígen de su búsqueda (API)</h3> }
        <GameCard />
        <GameCard />
      </div>
      <div className="p_btn_area">
        <button className="p_button">Anterior</button>
        <button className="p_button">Siguiente</button>
      </div>
    </div>
  )
}
