export const GET_API_PLATFORMS = "GET_API_PLATFORMS";
// export const GET_API_GENRES = "GET_GENRES";
// export const SEARCH_API_PLATFORMS = "GET_PLATFORMS";
// export const SEARCH_API_GENRES = "GET_GENRES";
// export const ADD_NEW_PLATFORM = "ADD_NEW_PLATFORM";
// export const ADD_NEW_GENRE = "ADD_NEW_GENRE";

// Request a la API para traer las películas.
// El payload va a ser el objeto que recibamos de nuestra request.

// Request a la API para traer las plataformas de videojuegos.
export function getApiPlatforms() {
  return function (dispatch) {
    return fetch("https://api.rawg.io/api/platforms") //url de api con plataformas
      .then(response => response.json()) //aplica json y retorna
      .then(json => {
        let data = json.results;
        let apiPlatfs = data.map(e => e.name);
        return apiPlatfs;
      })
      .then( platfs => {
        dispatch({ type: GET_API_PLATFORMS, payload: platfs }) // lo manda al reducer
      });
  };
}

// https://api.rawg.io/api/platforms
//.results  --> arr =  [0,...,50]
// arr[i].name  --> '...'
