import React, { useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import './CreateForm.css';
import MultiSelectPlatform from '../MultiSelectPlatform/MultiSelectPlatform';
import MultiSelectGenre from '../MultiSelectGenre/MultiSelectGenre';
import {saveNgGenSel,saveNgPlatfSel,sendedForm} from '../../actions/actions';
import axios from 'axios';

export default function CreateForm(props) {

  const dispatch = useDispatch();

  let [title, setTitle] = useState('');
  let [released, setReleased] = useState('');
  let [description, setDescription] = useState('');
  let [rating, setRating] = useState('');
  let [newplatforms, setNewplatforms] = useState(''); //OJO!!! []
  let [newgenres, setNewgenres] = useState('');//OJO!!! []

  function handleChange(e) {
    let nombre = e.target.name;
    switch (nombre) {
      case 'title':
        setTitle(e.target.value);
        break;
      case 'released':
        setReleased(e.target.value);
        break;
      case 'description':
        setDescription(e.target.value);
        break;
      case 'rating':
        setRating(e.target.value);
        break;
      case 'newplatforms':
        setNewplatforms(e.target.value);//ojo
        break;
      case 'newgenres':
        setNewgenres(e.target.value);//ojo
        break;
      default:
        break;
    }
  }

  const sgenres = useSelector(state => state.nggensel);
  const splatforms = useSelector(state => state.ngplatfsel);

  const handleSubmit = (event) => {
    event.preventDefault(); //sirve para evitar que refresque la pagina cada vez que apreto una tecla (comportamiento por default)
    let gametoadd = { title, released, description, rating, sgenres , splatforms};
    if (newplatforms !== '') {
      gametoadd = { ...gametoadd, newplatforms };
    }
    if (newgenres !== '') {
      gametoadd = { ...gametoadd, newgenres };
    }
    console.log(gametoadd.splatforms);

    axios.post('http://localhost:3001/newvideogame', gametoadd)
      .then(resp => {
        console.log(resp.data);
      })

    alert('juego agregado');
    setTitle('');
    setReleased('');
    setDescription('');
    setRating('');
    setNewplatforms('');
    setNewgenres('');
    dispatch(saveNgGenSel([]));
    dispatch(saveNgPlatfSel([]));
    dispatch(sendedForm(true));
  }



  return (
    <div className="ng_page">
      <div className="ng_container">
        <h1 className="ng_title"  >Nuevo juego: </h1>
        {/* <form method="post" action="http://localhost:3001/newrecipe" encType = "multipart / form-data" className="ng_formArea" onSubmit={handleSubmit}> */}
        <form method="post" action="http://localhost:3001/newgame" className="ng_formArea" onSubmit={handleSubmit}>
          <input
            id="input_name"
            type="text"
            className="ng_forminput"
            value={title}
            name='title'
            placeholder="Ingrese el nombre del juego..."
            onChange={handleChange}>
          </input>
          <input
            id="input_released"
            type="text"
            className="ng_forminput"
            value={released}
            name='released'
            placeholder="Fecha de lanzamiento..."
            onChange={handleChange}>
          </input>
          <textarea
            id="input_resume"
            className="ng_textinput"
            value={description}
            name='description'
            placeholder="Breve resúmen ..."
            type="text"
            onChange={handleChange}>
          </textarea>
          <input
            id="input_rating"
            type="text"
            className="ng_forminput"
            value={rating}
            name='rating'
            placeholder="Puntaje..."
            onChange={handleChange}>
          </input>
          <div className="ng_selectArea">
            <MultiSelectPlatform />
            <input
              id="input_name"
              type="text"
              className="anpforminput"
              value={newplatforms}
              name='newplatforms'
              placeholder="Nuevas plataformas (separadas por coma)..."
              onChange={handleChange}>
            </input>
            <MultiSelectGenre />
            <input
              id="input_name"
              type="text"
              className="angforminput"
              value={newgenres}
              name='newgenre'
              placeholder="Nuevos géneros (separadas por coma)..."
              onChange={handleChange}>
            </input>
          </div>
          <div className="ng_button-area">
            <button className="ng_button" type="submit">Guardar</button>
          </div>
        </form>
      </div>
    </div>
  )
};
