import React, { useState , useEffect,useRef } from 'react';
import { useDispatch , useSelector} from 'react-redux';
import './SearchBar.css';
import { titleSearch } from '../../actions/actions';

export default function SearchBar() {

  const dispatch = useDispatch();

  const sent = useSelector(state => state.initsearch);

  const [game,setGame] = useState("");

  // const handleSubmit = (event) => {
  //   event.preventDefault(); //sirve para evitar que refresque la pagina cada vez que apreto una tecla (comportamiento por default)
  //  // props.onSearch(city);
  //   setGame(""); //para limpiar el input
  // }   SEARCH_TITLE

  // Registra cambios en el input de búsqueda 
  // y lo guarda en el estado local del componente
  const handleChange = (e) => {
    setGame(e.target.value);
  }

  // Cada vez que presione buscar cambie el estado local lo guarda en el store
  let gref = useRef(game);
  useEffect(() => {
    dispatch(titleSearch(gref.current));
  },[sent,dispatch]);

  return (
    <>
      <input className="search_input"
        type="text" 
        placeholder=" Ingrese el nombre del juego..." 
        value = {game}
        onChange = {handleChange}
      />
    </>
  )
};
