import { React, useState } from 'react';
import './MultiSelectPlatform.css';

export default function MultiSelectPlatform() {
  const platfopt = [
    { value: 'chocolate', label: 'Chocolate' },
    { value: 'strawberry', label: 'Strawberry' },
    { value: 'vanilla', label: 'Vanilla' }
  ]

  let [platforms, setPlatforms] = useState([]);

  function handleSelChange(e) {
    //setPlatform(e.target.value);
    let itemP = e.target.value;
    alert(`click en ${itemP}`);
    onItemClick(itemP)
  }

  function onItemClick(item) {
    let hasItem = platforms.includes(itemP);
    if(!hasItem){
      setPlatforms([... platforms, itemP]);
    }else{
      let newItems = platforms.filter(item => item !== itemP);
      setPlatforms(newItems);
    }
  } 

  function checkSelect(texto) {}

  return (
    <div className="selplatfom">
      <select
        name="platforms"
        placeholder="Plataformas..."
        className="multselectpf"
        value={platforms}
        onChange={handleSelChange}
      >
        <option selected>Plataformas</option>
        {platfopt.map((item, index) => (
          <option key={index} value={item.value}>{item.label}</option>
        ))}
      </select>
      <div className="platfcont">
        <button className="selplatfbtn">AcaBoton</button>
      </div>
    </div>
  )
}
