import React from 'react';
import { useParams } from "react-router";
import { useLocation } from "react-router-dom";
import './GDInfo.css';
import star from '../../imgs/star.png';
//import axios from 'axios';

export default function GDInfo() {

  let { id } = useParams(); // <-- Retorna un objeto, acá es {id: Nro}

  let urlQuery = new URLSearchParams(useLocation().search);  //<-- Retorna un objeto
  let orig = urlQuery.get('orig'); //obtiene el query cuya key es 'orig'.

  async function getGameinfo(id, orig) {
    if (orig === 'API') {
      let objg = await fetch(`http://localhost:3001/videogame/${id}`)
        .then(response => response.json())
        .then(obj => {
          let { id, name, description, released, img_url, rating, platforms, genres } = obj;
          let genrenames = genres.map(e => e.name);
          let str = `${genrenames.toString().replace(/,/g, ', ')}`;
          return { id, name, description, released, img_url, rating, platforms, genres: str }
        })
      return objg;

    } else if (orig === 'base%20de%20datos') {
      let objg = await fetch(`http://localhost:3001/dbvideogame/${id}`)
        .then(response => response.json())
        .then(obj => {
          let { id, name, description, released, img_url, rating, platforms, genres } = obj;
          let genrenames = genres.map(e => e.name);
          let str = `${genrenames.toString().replace(/,/g, ', ')}`;
          return { id, name, description, released, img_url, rating, platforms, genres: str }
        })
    }

  }

  // let q = getQueryInfo(); // orig=API
  // let query = q.replace(/orig=/g,'')
  // return query;

  // function handleClick(e) {
  //   alert('elegiste volver');
  // }

  // let id = 6;
  // let title = "Nombre";
  // let URL_GAME = `...`;
  // let [game, setGame] = useState([]);

  return (
    <div>
      <div className="gd_picture" ></div>
      <div className="gd_picture-info">
        <div className="star">
          <img className="star_img" src={star} alt="star" />
          <p className="gdpT"><b className="gdt">8</b></p>
        </div>
        <p className="gdpT"><b className="gdt">Fecha de lanzamiento:</b>fecha </p>
        <p className="gdpT"><b className="gdt">Género:</b>generos </p>
        <p className="gdpT"><b className="gdt">Plataformas:</b>plataformas </p>
        <p className="gdpT"><b className="gdt">Descripción:</b>Descripción</p>
        <div>
        </div>
      </div>
    </div>
  )
}
