import React, { useState, useEffect } from 'react';
import { useDispatch } from 'react-redux';
import './OrigSelect.css';
import { setOrig } from '../../actions/actions';

export default function OriginSelect() {
  const dispatch = useDispatch();
  const origarr = [
    { label: 'Base de datos', value: 'db' },
    { label: 'API', value: 'api' }
  ];

  let [orig, setOrig] = useState('');

  function handleChange(e) {
    setOrig(e.target.value);
  }

  //Cada vez que cambie el origen guarda el estado en el store
  useEffect(() => {
    dispatch(setOrig(orig));
  }, [orig, dispatch]);

  return (
    <div className="ocont">
      <select
        name="origin"
        className="oselect"
        value={orig}
        onChange={handleChange}
      >
        <option defaultValue >Origen:</option>
        {origarr.map((option, index) => (
          <option key={index} value={option.value}>{option.label}</option>
        ))}
      </select>
    </div>
  )
}
