import React, { useEffect } from 'react';
import { useSelector } from 'react-redux';
import './GameCard.css';
import star from '../../imgs/star.png';
import { Link } from 'react-router-dom';


// {g.id} name={g.name} img={g.img_url} genres={g.genres}

export default function GameCard({ id, name, rating, img, genres }) {
  //console.log(props.game); 

  let orig = useSelector(state => state.searchOrig);

  function handleClick(e) {
    alert('elegiste juego');
    if (orig === 'API') {
      console.log('busc치 juego en API');
    }
    if (orig === 'base de datos') {
      console.log('busc치 juego en DB');
    }
  }

  return (
    <div className="gcardcont" onClick={handleClick}>
      <Link to='/gamedetail/:id' className="link_ng">
        <p className="gcname">{name}</p>
      </Link>
      <div className="gcrating">
        <img className="gcstar_img" src={star} alt="star" />
        <p className="gcrating_n"><b className="grt">{rating}</b></p>
      </div>
      {/* <img className="gImg" src={img} alt="game_image" /> */}
      <p className="pgenre"> {genres} </p>
    </div>
  )
}
