    case SET_NG_GEN_SEL:
      return {
        ...state,
        nggensel: action.payload
      }
    case SET_NG_PTF_SEL:
      return {
        ...state,
        ngplatfsel: action.payload
      }
    case SEND_FORM:
      return {
        ...state,
        sended: action.payload
      }
    //-----Search game----
    case INIT_SEARCH:
      return {
        ...state,
        initsearch: action.payload
      }
    case CLEAN_SEARCH:
