        gamestoshow: action.payload
      }
    case ADD_NG_GEN:
      return {
        ...state,
        addedgenres: action.payload
      }
    case ADD_NG_PLTF:
      return {
        ...state,
        addedplatfms: action.payload
      }


    default:
      return state;
  }
};

export default reducer;
