import React, { useState, useEffect } from 'react';
import './Ordering.css';
import { useDispatch } from 'react-redux';
import { setOrderOpt } from '../../actions/actions';

export default function Ordering() {

  const dispatch = useDispatch();

  let orderOp = [
    { label: "abc creciente", value: "abcCreciente" },
    { label: "abc decreciente", value: "abcDecreciente" },
    { label: "Puntaje creciente", value: "nroCreciente" },
    { label: "Puntaje decreciente", value: "nroDecreciente" },
  ];

  let [order, setOrder] = useState(''); // <---

  // function ordena(options, arr) {
  //   //console.log(arr);
  //   // console.log(options);
  //   let busqueda = options.title.toLowerCase();
  //   let farr = arr.filter(e => e.title.toLowerCase().includes(busqueda));
  //   if ((options.order === 'abcCreciente') || (options.order === 'abcDecreciente')) {
  //     let ordarr = farr.sort(function (a, b) {
  //       if (a.title.toLowerCase() > b.title.toLowerCase()) return 1;
  //       if (a.title.toLowerCase() < b.title.toLowerCase()) return -1;
  //       return 0;
  //     });
  //     if (options.order === 'abcDecreciente') ordarr = ordarr.reverse();
  //     return ordarr;
  //   } else if ((options.order === 'nroCreciente') || (options.order === 'nroDecreciente')) {
  //     let ordarr = farr.sort((a, b) => a.healthScore - b.healthScore);
  //     if (options.order === 'nroDecreciente') ordarr = ordarr.reverse();
  //     return ordarr;
  //   }
  //   //return farr;
  // }

  // Según selección determina el orden de búsqueda
  function handleChange(e) {
    setOrder(e.target.value);
  }

  //Cada vez que cambie el orden guarda el estado en el store
  useEffect(() => {
    dispatch(setOrderOpt(order));
  }, [order, dispatch]);

  return (
    <div className="ord_cont">
      <select
        name="orderO"
        className="ord_select"
        value={order}
        onChange={handleChange}
      >
        <option defaultValue>Ordenamiento:</option>
        {orderOp && orderOp.map((option, index) => (
          <option key={index} value={option.value}>{option.label}</option>
        ))}
      </select>
    </div>
  )
}
