import React, { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import './MultiSelectGenre.css';
import store from '../../store/index';
//import genresopt from '../Genres/Genres';
import { getDbGenres } from '../../actions/actions';

export default function MultiSelectGenre() {

  const dispatch = useDispatch();
  const loading = useSelector(state => state.loading);

  // (1)Cuando cargue el componente que despache la acción 
  // que busca los géneros.
  useEffect(() => {
    dispatch(getDbGenres());
    console.log('(1) se cargó la página');
  }, []);

  // Se trae el el arreglo de géneros del store.
  const genres = useSelector(state => state.genreopt);

  let [genreop, setGenreop] = useState([]);

  let [show, setShow] = useState(false);

  // (2) Cuando cargó los géneros en (1) que cambie el estado "show" a true
  useEffect(() => {
    setGenreop(genres);
    setShow(true);
    console.log('(2)cambió show a true');
  }, [store.getState().loading])

  // const genres = useSelector(state => state.genreopt);
  // console.log(genres);


  useEffect(() => {

    //console.log(genreopt);
    //setShow(true);
  }, [show])

  console.log(show);
  // Estado de géneros seleccionados
  let [sgenres, setSgenres] = useState([]);// selected genres

  function removeItem(item) {
    let newItems = sgenres.filter(e => e !== item);
    setSgenres(newItems);
  }

  async function handleChange(e) {
    let itemP = e.target.value;
    let hasItem = sgenres.includes(itemP);
    if (!hasItem) {
      setSgenres([...sgenres, itemP]);
    } else {
      removeItem(itemP);
    }
  }

  function handleClick(e) {
    let itm = e.target.value;
    removeItem(itm);
  }

  useEffect(() => {
  }, [sgenres]);

  return (
    <div className="selgenfom">
      {!show ? 'no muestra' :
        <>
          <select
            name="genres"
            placeholder="Plataformas..."
            className="multselectgen"
            value={''}
            onChange={handleChange}
          >
            <option defaultValue>Géneros:</option>
            {genreop && genreop.map((item, index) => (
              <option key={index} value={item.value}>{item.label}</option>
            ))}
          </select>
          <div className="gencont">
            {sgenres.map((item, index) => (
              <button className="selgenbtn" value={item}
                onClick={handleClick} key={index}>
                {item}
              </button>
            ))}
          </div>
        </>
      }
    </div>
  )
}
