import React,{ useEffect }  from 'react';
import { useLocation } from 'react-router-dom';
import GDLinks from '../GDLinks/GDLinks'; 
import NGLinks from '../GDLinks/GDLinks';
import HLinks from '../GDLinks/GDLinks';
import './PLinks.css';

export default function PLinks(props) {

	const location = useLocation();
	useEffect(() => {
		const currentPath = location.pathname;
		//console.log(`currentPath:${currentPath}`)
	}, [location]);

	// const Linkbutton = (pagina) => ({

	// })
	let pagina = location.pathname.slice(1);
	console.log(pagina);

	let ishome = pagina === 'home';
	let isnewgame = pagina === 'newgame';
	let isgamedetail = pagina.split('/')[0] === 'gamedetail';

 return (
	<div className="links">
		{ ishome && <HLinks/>}
		{	isnewgame && <NGLinks/>}
		{/* { isgamedetail && <GDLinks/>} */}
		</div>
  )
};
