import React, { useState, useEffect, useRef } from 'react';
import { useSelector } from 'react-redux';
//import { useEffect, useState } from 'react';
//import { useSelector, useDispatch } from 'react-redux'
import './CreateForm.css';
import MultiSelectPlatform from '../MultiSelectPlatform/MultiSelectPlatform';
import MultiSelectGenre from '../MultiSelectGenre/MultiSelectGenre';
// import AddNewPlatform from '../AddNewPlatform/AddNewPlatform';
// import AddNewGenre from '../AddNewGenre/AddNewGenre';
//import { connect } from "react-redux";
//import {getDbGenres} from '../../actions/actions';
import axios from 'axios';
import store from '../../store/index';

export default function CreateForm(props) {
  //   // console.log(props);
  //   // props.findgenres();
  //   // console.log(props.loading);
  //   const dispatch = useDispatch();
  //   dispatch(getDbGenres());

  //   console.log(store.getState());
  //   // store.dispatch(getDbGenres());
  //   //console.log(store.getState())

  // //   useEffect(() => {
  // //   dispatch(getDbGenres)
  // // },[])

  let [title, setTitle] = useState('');
  let [released, setReleased] = useState('');
  let [description, setDescription] = useState('');
  let [rating, setRating] = useState('');
  let [newplatform, setNewplatform] = useState('');
  let [newgenre, setNewgenre] = useState('');

  function handleInputChange(e) {
    let nombre = e.target.name;
    switch (nombre) {
      case 'title':
        setTitle(e.target.value);
        break;
      case 'released':
        setReleased(e.target.value);
        break;
      case 'description':
        setDescription(e.target.value);
        break;
      case 'rating':
        setRating(e.target.value);
        break;
      case 'newplatform':
        setNewplatform(e.target.value);
        break;
      case 'newgenre':
        setNewgenre(e.target.value);
        break;
      default:
        break;
    }
  }

  // const genres = useSelector(state => state.nggensel);
  // const platforms = useSelector(state => state.ngplatfsel);

  // //console.log(rating);
  // let state1 = {title, released,description,rating};
  //ojo!!!
  // let loading = true; //ojo!!!



  const handleSubmit = (event) => {
    event.preventDefault(); //sirve para evitar que refresque la pagina cada vez que apreto una tecla (comportamiento por default)
    let gametoadd = { title, released, description, rating };
    if (newplatform !== '') {
      gametoadd = { ...gametoadd, newplatform };
    }
    if (newgenre !== '') {
      gametoadd = { ...gametoadd, newgenre };
    }


    axios.post('http://localhost:3001/newgame', gametoadd)
      .then(resp => {
        console.log(resp.data);
      })
    // props.onSearch(city);
    //setGame(""); //para limpiar el input
    alert('juego agregado');
    setTitle('');
    setReleased('');
    setDescription('');
    setRating('');
    setNewplatform('');
    setNewgenre('');
  }
// const genres = useRef(useSelector(state => state.nggensel));
//let genres = [];
  useEffect(() => {
    //console.log(genres);
    genres = store.getState().nggensel;
    console.log(store.getState().nggensel)
  }, [store.getState().nggensel]);

  //const sgenres = store.getState().nggensel;

  console.log(genres);

  return (
    <div className="ng_page">
      <div className="ng_container">
        <h1 className="ng_title"  >Nuevo juego: </h1>
        {/* <form method="post" action="http://localhost:3001/newrecipe" encType = "multipart / form-data" className="ng_formArea" onSubmit={handleSubmit}> */}
        <form method="post" action="http://localhost:3001/newgame" className="ng_formArea" onSubmit={handleSubmit}>
          <input
            id="input_name"
            type="text"
            className="ng_forminput"
            value={title}
            name='title'
            placeholder="Ingrese el nombre del juego..."
            onChange={handleInputChange}>
          </input>
          <input
            id="input_released"
            type="text"
            className="ng_forminput"
            value={released}
            name='released'
            placeholder="Fecha de lanzamiento..."
            onChange={handleInputChange}>
          </input>
          <textarea
            id="input_resume"
            className="ng_textinput"
            value={description}
            name='description'
            placeholder="Breve resúmen ..."
            type="text"
            onChange={handleInputChange}>
          </textarea>
          <input
            id="input_rating"
            type="text"
            className="ng_forminput"
            value={rating}
            name='rating'
            placeholder="Puntaje..."
            onChange={handleInputChange}>
          </input>
          <div className="ng_selectArea">
            <MultiSelectPlatform />
            <input
              id="input_name"
              type="text"
              className="anpforminput"
              value={newplatform}
              name='newplatform'
              placeholder="Ingrese plataforma no existente..."
              onChange={handleInputChange}>
            </input>
            <MultiSelectGenre />
            <input
              id="input_name"
              type="text"
              className="angforminput"
              value={newgenre}
              name='newgenre'
              placeholder="Ingrese género no existente..."
              onChange={handleInputChange}>
            </input>
          </div>
          <div className="ng_button-area">
            <button className="ng_button" type="submit">Guardar</button>
            {/* <input type="submit" value="Guardar" /> */}
          </div>
        </form>
      </div>
    </div>
  )
};
