import React, { useState, useEffect } from 'react';
import './MultiSelectGenre.css';
import genresopt from '../Genres/Genres';

export default function MultiSelectGenre() {
  const platfopt = [
    { value: 'chocolate', label: 'Chocolate' },
    { value: 'strawberry', label: 'Strawberry' },
    { value: 'vanilla', label: 'Vanilla' }
  ]

  let [genres, setGenres] = useState([]);

  function removeItem(item) {
    let newItems = genres.filter(e => e !== item);
    setGenres(newItems);
  }

  async function handleChange(e) {
    let itemP = e.target.value;
    let hasItem = genres.includes(itemP);
    if (!hasItem) {
      setGenres([...genres, itemP]);
    } else {
      removeItem(itemP);
    }
  }

  function handleClick(e) {
    let itm = e.target.value;
    removeItem(itm);
  }

  useEffect(() => {
  }, [genres]);

  return (
    <div className="selgenfom">
      <select
        name="genres"
        placeholder="Plataformas..."
        className="multselectgen"
        value={''}
        onChange={handleChange}
      >
        <option defaultValue>GÃ©neros:</option>
        {platfopt.map((item, index) => (
          <option key={index} value={item.value}>{item.label}</option>
        ))}
      </select>
      <div className="gencont">
        {genres.map((item, index) => (
          <button className="selgenbtn" value={item}
            onClick={handleClick} key={index}>
            {item}
          </button>
        ))}
      </div>
    </div>
  )
}


