import React, { useState, useEffect } from 'react';
import './MultiSelectPlatform.css';
import { connect } from "react-redux";
import { getApiPlatforms } from '../../actions/actions';

export function MultiSelectPlatform(props) {
  //console.log(props);
  //const platfopt = props.getApiPlatforms();

  const plataformas = [];
  const plataformasO = [];

  function getPlatforms() {
    fetch("https://api.rawg.io/api/platforms") //url de api con plataformas
      .then(response => response.json()) //aplica json y retorna
      .then(json => {
        let data = json.results;
        for (let i=0 ; i< data.length; i++){
          plataformas.push(data[i].name)
          let obj = {value: '',label: '' };
          obj.value=data[i].name;
          obj.la
          plataformasO.push(toJson(`{value: "${data[i].name}", label: "${data[i].name}"}`))
        }
      })
      .catch(error => console.log(error))
    return
  }

  getPlatforms();
  console.log(plataformasO);

  // function createPlatfOpts(arr) {
  //   let p = arr;
  //   console.log(p);
  //   console.log(`${p[0]}`);
  //   const platfOpts = [];
  //   for (let i=0 ; i< p.length; i++){
  //     let a =  p[i];
  //    // console.log(a);
  //     platfOpts.push( a );
  //     // platfOpts.push( {value: p[i], label: p[i]} );
  //   }
  //   //console.log(platfOpts);
  //   return platfOpts;
  // }

 // createPlatfOpts(plataformas);
  //let platfopt = createPlatfOpts(plataformas);
 // console.log(platfopt);

  let [splatforms, setSplatforms] = useState([]); // selected platforms

  function removeItem(item) {
    let newItems = splatforms.filter(e => e !== item);
    setSplatforms(newItems);
  }

  async function handleChange(e) {
    let itemP = e.target.value;
    let hasItem = splatforms.includes(itemP);
    if (!hasItem) {
      setSplatforms([...splatforms, itemP]);
    } else {
      removeItem(itemP);
    }
  }

  function handleClick(e) {
    let itm = e.target.value;
    removeItem(itm);
  }

  useEffect(() => {
  }, [splatforms]);

  return (
    <div className="selplatfom">
      <select
        name="platforms"
        placeholder="Plataformas..."
        className="multselectpf"
        value={''}
        onChange={handleChange}
      >
        <option defaultValue>Plataformas:</option>
        {/* {platfopt.map((item, index) => (
          <option key={index} value={item.value}>{item.label}</option>
        ))} */}
      </select>
      <div className="platfcont">
        {splatforms.map((item, index) => (
          <button className="selplatfbtn" value={item}
            onClick={handleClick} key={index}>
            {item}
          </button>
        ))}
      </div>
    </div>
  )
}

// Función que recibe como argumento el estado de Redux y retorna
// como "props" un objeto que contiene el estado.
const mapStateToProps = state => {
  return { platforms: state.apiplatforms }
}

// // Función que recibe como argumento el método dispatch() de Redux
// // y retorna un objeto.

// const mapDispatchToProps = dispatch => {
// 	return { getplatforms: () => dispatch(getApiPlatforms()) }
// }

export default connect(mapStateToProps, { getApiPlatforms })(MultiSelectPlatform);
