import  React, {useState , useEffect} from 'react';
import './MultiSelectPlatform.css';
import {connect} from "react-redux";
import { getApiPlatforms } from '../../actions/actions';

export default function MultiSelectPlatform() {
  const platfopt = [
    { value: 'chocolate', label: 'Chocolate' },
    { value: 'strawberry', label: 'Strawberry' },
    { value: 'vanilla', label: 'Vanilla' }
  ]

  let [platforms, setPlatforms] = useState([]);

  function removeItem(item) {
    let newItems = platforms.filter(e => e !== item);
    setPlatforms(newItems);
  }

  async function handleChange(e) {
    let itemP = e.target.value;
    let hasItem = platforms.includes(itemP);
    if (!hasItem) {
      setPlatforms([...platforms, itemP]);
    } else {
      removeItem(itemP);
    }
  }

  function handleClick(e) {
    let itm = e.target.value;
    removeItem(itm);
  }

	useEffect(() => {
	}, [platforms]);

  return (
    <div className="selplatfom">
      <select
        name="platforms"
        placeholder="Plataformas..."
        className="multselectpf"
        value={''}
        onChange={handleChange}
      >
        <option defaultValue>Plataformas:</option>
        {platfopt.map((item, index) => (
          <option key={index} value={item.value}>{item.label}</option>
        ))}
      </select>
      <div className="platfcont">
        {platforms.map((item, index) => (
          <button className="selplatfbtn" value={item}
            onClick={handleClick} key={index}>
            {item}
          </button>
        ))}
      </div>
    </div>
  )
}
