export const GET_PLATFORMS = "GET_PLATFORMS";
export const GET_GENRES = "GET_GENRES";
export const ADD_NEW_PLATFORM = "ADD_NEW_PLATFORM";
export const ADD_NEW_GENRE = "ADD_NEW_GENRE";

// Request a la API para traer las películas.
// El payload va a ser el objeto que recibamos de nuestra request.
// Request para traer las plataformas de la base de datos.
export function getPlatforms(titulo) {
  return function(dispatch) {
    return fetch("http://www.omdbapi.com/?apikey=20dac387&s=" + titulo) //url para pedir por tìtulo
    .then(response => response.json()) //aplica json y retorna
    .then(json => {
      dispatch({ type: GET_MOVIES, payload: json.Search }); // lo manda al reducer
    });
  };
}

// https://api.rawg.io/api/platforms
//.results  --> arr =  [0,...,50]
