import React, { useState, useEffect, useRef } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import './Pagination.css';
import GameCard from '../GameCard/GameCard.jsx';
import { getApiGames, getdbGames , initsearch} from '../../actions/actions';

export default function Pagination() {

  const dispatch = useDispatch();

  const origin = useSelector(state => state.searchOrig);
  const apiGames = useSelector(state => state.apigames);
  const dbGames = useSelector(state => state.dbgames);
  const search_gen = useSelector(state => state.searchgenre);
  const search_t = useSelector(state => state.stitle);
  const sent = useSelector(state => state.initsearch);
  const cleancards = useSelector(state => state.cleancards);

  const [showMsg, setShowMsg] = useState(true);
  const [searchingG, setSearchingG] = useState(false);
  const [games, setGames] = useState([]);
  const [genFiltGames, setGenFiltGames] = useState(null);
  const [titFiltGames, setTitFiltGames] = useState(null);

  // Según el origen busca el arreglo de juegos
  // una vez elegido origen desactiva el mje de sel. orig. y activa 
  // el mje de obteniendo juegos.
  useEffect(() => {
    if (origin === 'api') {
      // console.log('cargaste API');
      dispatch(getApiGames());
      setShowMsg(false);// desactiva mje de seleccionar origen
      setSearchingG(true); // activa mje de buscando
      //limpiar dbgames
    }
    if (origin === 'db') {
      console.log('falta cargar videojuegos en la base de datos');
      // dispatch(getdbGames());
      setShowMsg(false);
      setSearchingG(true);
      //limpiar apigames
    }
  }, [origin, dispatch])

  let gRef = useRef(games);
  useEffect(() => {
    //console.log(gRef.current)
  }, [showMsg, searchingG])

  let origRef = useRef(origin);
  // Una vez cargados los juegos en el store desactiva mje "buscando"
  // y guarda el arreglo en el estado local games.
  useEffect(() => {
    setSearchingG(false);
    if(origRef.current === 'api'){
      setGames(apiGames)
      console.log(`(1)${gRef.current}`);
    }
    if(origRef.current === 'db'){
      setGames(dbGames)
    }
  }, [apiGames, dbGames])

  useEffect(() => {
    console.log(`(2)${gRef.current}`);
  }, [games])

  // function genreFilter(arr, str) {
  //   let newarr = arr.filter(e => e.genres.includes(str));
  //   return newarr;
  // }


  // // Si se especifica un género para filtar llama a genreFilter
  // useEffect(() => {
  //   let newarr = genreFilter(gRef.current, search_gen);
  //   setGenFiltGames(newarr);
  //   setTitFiltGames(null); // Si cambio el género que quite cualquier selección previa guardada por título.
  //   //limpiar area
  // }, [search_gen])

  // function titleFilter(arr, str) {
  //   let newarr = arr.filter(e => e.name.includes(str));
  //   setTitFiltGames(newarr);
  //   return newarr;
  // }

  // let gfg = useRef(genFiltGames)
  // let tref= useRef(search_t)
  // let tfgRef = useRef(titFiltGames);
  // useEffect(() => {
  //   let newarr = titleFilter(gfg.current||gRef.current, tref.current);// Si no filtro por género que deje los de la api
  //   setTitFiltGames(newarr);
  //   // console.log(tfgRef.current);
  //   // console.log(sent);
  // }, [sent]) // en submit

  // useEffect(()=>{
  //   dispatch(initsearch(false)); // limpia????
  //   // setShowMsg(true);// desactiva mje de seleccionar origen
  //   // setSearchingG(false);
  // },[cleancards,dispatch])

  // // traerme el arreglo
  // // traerme el género y el título a buscar
  // // hacer el filtrado
  // // mostrar de a 6
  // // 

  // // si es api y el texto de búsqueda es '' trae 100 de la ruta local / videogames


  return (
    <div>
      {showMsg && <h3> Seleccione primero un orígen para su búsqueda (API/DB)</h3>}
      {searchingG && <h3> ...Obteniendo juegos de origen</h3>}
      {cleancards && <div className="p_cards_area">
        <GameCard />
        <GameCard />
      </div>}
      <div className="p_btn_area">
        <button className="p_button">Anterior</button>
        <button className="p_button">Siguiente</button>
      </div>
    </div>
  )
}
