import React, { useState, useEffect } from 'react';
import { useDispatch } from 'react-redux';
import { useSelector } from 'react-redux';
import './MultiSelectGenre.css';
import store from '../../store/index';
//import genresopt from '../Genres/Genres';
import { getDbGenres } from '../../actions/actions';

export default function MultiSelectGenre() {


  //   const [genreop, setGenreop] = useState([]);

  let [show, setShow] = useState(false);

  const genreopt = [];

  async function buildGenObjArr() {

    fetch("http://localhost:3001/genres")
      .then(response => response.json())
      .then(async arr => {
         for (let i = 0; i < arr.length; i++) {
          let obj = { value: arr[i].name, label: arr[i].name };
          genreopt.push(obj);
       }
        console.log(genreopt);
      })
      .catch(error => console.log(error))
  }

  // useEffect(async () => {
  //   await buildGenObjArr();
  //   // console.log(genreopt);
  //   // console.log('(3) Se trajo el arreglo');
  //   // setShow(true);
  // }, [])


  // // const state = useSelector(state => state.genreopt);
  // // console.log(state);

  //   // (1) Cuando cargue el componente que despache la acción 
  //   // que busca los géneros.
  //   useEffect(() => {
  //     console.log('(1) se cargó la página');
  //     store.dispatch(getDbGenres());
  //   }, []);

  //   const [genreop, setGenreop] = useState([]);

  //   let [show, setShow] = useState(false);

  //   //(2) Cuando cargó los géneros en (1) que cambie el estado "show" a true
  //   useEffect(() => {
  //     console.log('(2) El store se cargó');
  //     const genres = store.getState().genreopt;
  //     //console.log(genres);
  //     setGenreop(genres);
  //     //console.log(genreop);
  //   }, [store.getState().genreopt]).then(()=>      console.log('(3) Se trajo el arreglo'))

  //   //console.log(genres);

  //   useEffect(() => {
  //     console.log('(3) Se trajo el arreglo');
  //     console.log(genreop);
  //     setShow(true);
  //   }, [genreop])

  //console.log('(2)cambió show a true, loading='+store.getState().loading);

  // const genres = useSelector(state => state.genreopt);
  // console.log(genres);


  // useEffect(() => {
  //   console.log(genreop);
  //   setShow(true);
  // }, [show,genreop])

  console.log(show);


  // Estado de géneros seleccionados
  let [sgenres, setSgenres] = useState([]);// selected genres

  function removeItem(item) {
    let newItems = sgenres.filter(e => e !== item);
    setSgenres(newItems);
  }

  async function handleChange(e) {
    let itemP = e.target.value;
    let hasItem = sgenres.includes(itemP);
    if (!hasItem) {
      setSgenres([...sgenres, itemP]);
    } else {
      removeItem(itemP);
    }
  }

  function handleClick(e) {
    let itm = e.target.value;
    removeItem(itm);
  }

  useEffect(() => {
  }, [sgenres]);

  return (
    <div className="selgenfom">
      {!show ? 'no muestra' :
        <>
          <select
            name="genres"
            placeholder="Plataformas..."
            className="multselectgen"
            value={''}
            onChange={handleChange}
          >
            <option defaultValue>Géneros:</option>
            {genreopt && genreopt.map((item, index) => (
              <option key={index} value={item.value}>{item.label}</option>
            ))}
          </select>
          <div className="gencont">
            {sgenres.map((item, index) => (
              <button className="selgenbtn" value={item}
                onClick={handleClick} key={index}>
                {item}
              </button>
            ))}
          </div>
        </>
      }
    </div>
  )
}
