import React from 'react';
import './SearchMenu.css';
import { useDispatch } from 'react-redux';
import GenreSelect from '../GenreSelect/GenreSelect';
import OrigSelect from '../OrigSelect/OrigSelect';
import SearchBar from '../SearchBar/SearchBar.jsx';
import Ordering from '../Ordering/Ordering.jsx';
import { initsearch ,cleansearch} from '../../actions/actions';

export default function SearchMenu() {

  const dispatch = useDispatch();

  const handleSubmit = (event) => {
    event.preventDefault(); //sirve para evitar que refresque la pagina cada vez que apreto una tecla (comportamiento por default)
    console.log('iniciaste búsqueda');
    dispatch(initsearch(true));
  }

  const handleClick =(event) => {
    console.log('nueva búsqueda');
    dispatch(initsearch(false));
    dispatch(cleansearch(true));
  //  dispatch(setOrigOpt(''));
  //  dispatch(searchGenre('')); 

  //setOrderOpt('')
   //limpia el resto
   //showMsg
  }

  return (
    <div className="search_area">
      <form  className="sform" onSubmit={handleSubmit}>
        <div className="search_menu">
          <OrigSelect/>
          <GenreSelect/>
          <Ordering/>
          <SearchBar/>
          <input className="search_btn" type="submit" value="Buscar"  />
        </div>
      </form>
      <hr className="separate"/>
          <button className="search_btn" onClick={handleClick}>Otra búsqueda</button>
    </div>
  )
};
