import React, { useState, useEffect, useRef } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import './GenreSelect.css';
import { dbGenOpSearch, apiGenOpSearch, setSearchGenre } from '../../actions/actions';

//used on SearchMenu

export default function GenreSelect() {

  const dispatch = useDispatch();

  const origin = useSelector(state => state.searchOrig);// <--Busca el origen del store.
  const apigenres = useSelector(state => state.apigenopts);

  const [genres, setGenres] = useState('');

  // const origRef = useRef(origin);
  // const apigenRef = useRef(apigenres);
  //console.log(apigenRef);


  const genreopts = (orig,apig) => {
    if (orig === 'db') {
      console.log(orig);
      dispatch(dbGenOpSearch());
    }
    if (orig === 'api') {
      console.log(orig);
      dispatch(apiGenOpSearch());
      setGenres(apig);
      //console.log(apig);
    }
  }

  //Cada vez que cambie el origen guarda el estado en el store
  useEffect(() => {

    genreopts(origin,apigenres);
  },[origin]);



  // const genres = [
  //   { label: 'acción', value: 'acción' },
  //   { label: 'aventura', value: 'aventura' }
  // ];

  // Género a buscar
  function handleChange(e) {
    setGenres(e.target.value);
    dispatch(setSearchGenre());
  }



  return (
    <div className="gcont">
      <select
        name="genres"
        className="gselect"
        value={genres}
        onChange={handleChange}
      >
        <option defaultValue >Género:</option>
        {genres && genres.map((option, index) => (
          <option key={index} value={option.value}>{option.label}</option>
        ))}
      </select>
    </div>
  )
}
