import React , {useState , useEffect } from 'react';
import { useParams } from "react-router";
import { useLocation } from "react-router-dom";
import './GameDetail.css';
import Navbar from '../Navbar/Navbar';
import star from '../../imgs/star.png';

export default function GameDetail() {

  let { id } = useParams(); // <-- Retorna un objeto, acá es {id: Nro}

  let urlQuery = new URLSearchParams(useLocation().search);  //<-- Retorna un objeto
  let orig = urlQuery.get('orig'); //obtiene el query cuya key es 'orig'.
  console.log(id,orig)

  async function getGameinfo(id, orig) {
    if (orig === 'API') {
      await fetch(`http://localhost:3001/videogame/${id}`)
        .then(response => response.json())
        .then(obj => {
          let { id, name, description, released, img_url, rating, platforms, genres } = obj;
          let genrenames = genres.map(e => e.name);
          let str = `${genrenames.toString().replace(/,/g, ', ')}`;
          return { id, name, description, released, img_url, rating, platforms, genres: str }
        })
        .then(obj => {
          setGobj(obj);
        })
        .catch(error => console.log(error))


    } else if (orig === 'base%20de%20datos') {
      let objg = await fetch(`http://localhost:3001/dbvideogame/${id}`)
        .then(response => response.json())
        .then(obj => {
          let { id, name, description, released, img_url, rating, platforms, genres } = obj;
          let genrenames = genres.map(e => e.name);
          let str = `${genrenames.toString().replace(/,/g, ' - ')}`;
          return { id, name, description, released, img_url, rating, platforms, genres: str }
        })
        .then(obj => {
          setGobj(obj);
        })
        .catch(error => console.log(error))
    }

  }

  let [gobj, setGobj] = useState(null);


  useEffect(() => {
    getGameinfo(id, orig);
  }, [id,orig,getGameinfo])

  // let { name, description, released, img_url, rating, platforms, genres } = gobj;


  return (
    <div className="ng_page">
      <Navbar />
      <div className="gd_area">
        <h1 className="gd_title"  >{gobj.name} </h1>
        <img className="gd_picture" src={gobj.img_url} alt="game_img" />
        <div className="gd_picture-info">
          <div className="star">
            <img className="star_img" src={star} alt="star" />
            <p className="gdpT"><b className="gdt">{gobj.rating}</b></p>
          </div>
          <p className="gdpT"><b className="gdt">Fecha de lanzamiento:</b>{gobj.released}</p>
          <p className="gdpT"><b className="gdt">Género:</b>{gobj.genres} </p>
          <p className="gdpT"><b className="gdt">Plataformas:</b>{gobj.platforms} </p>
          <p className="gdpT"><b className="gdt">Descripción:</b>{gobj.description}</p>
          <div>
          </div>
        </div>
      </div>
    </div>
  )
}
