import React, { useState, useEffect, useRef } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import './GenreSelect.css';
import { dbGenOpSearch, apiGenOpSearch, setSearchGenre } from '../../actions/actions';

//used on SearchMenu

export default function GenreSelect() {

  const dispatch = useDispatch();
  const origin = useSelector(state => state.searchOrig);// <--Busca el origen del store.
  const apigenres = useSelector(state => state.apigenopts);
  const apigenRef = useRef(apigenres);//<--para evitar missing dependency en useEffect
  const dbgenres = useSelector(state => state.dbgenopts);
  const dbgenRef = useRef(dbgenres);

  const [genres, setGenres] = useState('');

  // Cada vez que cambie el origen guarda el estado en el store
  // y setea genres con los valores correspondientes
  useEffect(() => {
    const genreopts = (orig, apig, dbg) => {
      if (orig === 'db') {
        //console.log(orig);
        //dispatch(dbGenOpSearch()); // <-- Hacer...
        setGenres(dbg);
      }
      if (orig === 'api') {
        //console.log(orig);
        dispatch(apiGenOpSearch());
        // setGenres(apig);
        //console.log(apig);
      }
    }
    genreopts(origin, apigenRef.current, dbgenRef.current);// <-- referencia a apigenres
  }, [origin, dispatch]);

  //Cada vez que cambie el origen guarda el estado en el store
  useEffect(() => {
    setGenres(apigenres);
  }, [apigenres]);

  useEffect(() => {
    setGenres(dbgenres);
  }, [dbgenres]);

  // Género a buscar
  const [filtergen, setFiltergen] = useState('');

  function handleChange(e) {
    setSearchGenre(e.target.value);
    dispatch(setSearchGenre());
  }

  return (
    <div className="gcont">
      <select
        name="genres"
        className="gselect"
        value={genres}
        onChange={handleChange}
      >
        <option defaultValue  >Género:</option>
        {genres && genres.map((option, index) => (
          <option key={index} value={option.value}>{option.label}</option>
        ))}
      </select>
    </div>
  )
}
