import { getApiGames, getdbGames, initsearch } from '../../actions/actions';

export default function Pagination() {

  const dispatch = useDispatch();

  const inits = useSelector(state => state.initsearch);
  const cleans = useSelector(state => state.cleansearch);
  const origin = useSelector(state => state.searchOrig);
  const resolverRef = useRef(null);

  let [showMsg, setShowMsg] = useState(true);
  let [gettingG, setGettingG] = useState(false);



