import React, { useState, useEffect, useRef } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import './GamesGetter.css';
import GamesToShow from '../GamesToShow/GamesToShow.jsx';
import GameCard from '../GameCard/GameCard.jsx';
import { getApiGames, getdbGames, initsearch, setShowmsg, setGamesToShow } from '../../actions/actions';

export default function GamesGetter() {

  let dispatch = useDispatch();

  let inits = useSelector(state => state.initsearch);
  let cleans = useSelector(state => state.cleansearch);
  let orig = useSelector(state => state.searchOrig);
  let show = useSelector(state => state.showmsg);
  let apig = useSelector(state => state.apigames);
  let apigRef = useRef(apig); //---
  let dbg = useSelector(state => state.dbgames);
  let dbgRef = useRef(dbg); //---
  let sgenre = useSelector(state => state.searchgenre);
  let st = useSelector(state => state.stitle);
  let stRef = useRef(st); //---
  let gamests = useSelector(state => state.gamestoshow); // juegos para mostrar

  //let [showMsg, setShowMsg] = useState(true); //showmsg setShowmsg
  let [gettingG, setGettingG] = useState(false);
  let [games, setGames] = useState([]);
  let gRef = useRef(games); //---
  let [genFiltGames, setGenFiltGames] = useState(null);
  let gfgRef = useRef(genFiltGames); //---
  let [titFiltGames, setTitFiltGames] = useState(null);
  let tfgRef = useRef(titFiltGames); //---

  // Según el origen manda a buscar el arreglo de juegos 
  // donde corresponda y admninistra mjes.
  useEffect(() => {
    setGames([]);
    if (orig === 'API') {
      //console.log('cargaste API');
      // dispatch(getApiGames());  // <<-- Sacar!!
      dispatch(setShowmsg(false));
      setGettingG(true);
    }
    if (orig === 'base de datos') {
      // dispatch(getdbGames());  // <<-- Sacar!!
      dispatch(setShowmsg(false));
      setGettingG(true);
    }
  }, [orig, dispatch]);

  // Cuando obtiene el arreglo de juegos lo guarda localmente.
  // y administra mje-
  useEffect(() => {
    if (orig === 'API') {
      setGettingG(false);
      console.log(apigRef.current);
      setGames(apigRef.current);
    }
    if (orig === 'base de datos') {
      setGettingG(false);
      console.log(dbgRef.current);
      setGames(dbgRef.current);
    }
  }, [gettingG, orig]); //sacar gettingG y dejar apiG / dbg

  // Si se filtra por género reduce la búsqueda y guarda lo obtenido
  // en el estado genFiltGames.
  useEffect(() => {
    let newarr = games.filter(e => e.genres.includes(sgenre));
    setGenFiltGames(newarr);
    setTitFiltGames(null); // Si cambio el género que quite cualquier selección previa guardada por título.
    dispatch(initsearch(false)); // Cancela cualquier búsqueda anterior para poder escribir.
    // console.log('cleans,'+cleans+', inits,' +inits)
  }, [sgenre, games, dispatch])

  useEffect(() => {
    console.log(genFiltGames); //........
  }, [genFiltGames])

  // Cuando presiona "buscar" filtra lo que ya tenía según el título
  useEffect(() => {
    if (inits) { // Si presionó/está en buscar
      let arr = games; // arreglo obtenido según orígen
      if (!sgenre) {
        console.log('no había género de búsqueda');
      } else {// si filtró x género usa el arreglo ya filtrado
        //console.log(genFiltGames);
        arr = genFiltGames;
      }
      if (!st) {// si no ingreso texto trae todo
        console.log('no había texto de búsqueda');
        setTitFiltGames(arr);
      } else {
        console.log('search title is' + st);
        let newarr = arr.filter(e => e.name.toLowerCase().includes(st.toLowerCase()));
        setTitFiltGames(newarr);
      }
    }
  }, [inits, genFiltGames, st])

  useEffect(() => {
    if (cleans) {
      setTitFiltGames([]);
    }
  }, [cleans])

  useEffect(() => {
    console.log(titFiltGames);
    dispatch(setGamesToShow(titFiltGames));
    // console.log('(1)');
  }, [titFiltGames, dispatch])

  useEffect(() => {// borrar x qe es del store
  }, [gamests])
  //-----------------------------------------

  let [pageCount, setPageCount] = useState(0);
  let [currentPage, setCurrentPage] = useState(0); //inicialmente la primer página
  let [currentPageArr, setCurrentPArr] = useState([]);
  let PER_PAGE = 3;

  useEffect(() => {
    if (titFiltGames) {
      console.log('Toy acá 1')
      let n = titFiltGames.length;
      let pc = Math.ceil(n / PER_PAGE);
      setPageCount(pc);
    }
  }, [titFiltGames,PER_PAGE])

  useEffect(() => {
      // Función que obtiene el subarreglo de juegos a mostrar según la pagina
      // en la que se encuentre.
      function inpageshow(currentPage, perpage) {
        let offset = currentPage * perpage;
        let currentPageArr = games.slice(offset, offset + PER_PAGE)
        console.log(currentPageArr);
        setCurrentPArr([...currentPageArr]);
      }
      console.log('Toy acá 2')
      inpageshow(currentPage, PER_PAGE)
  },[currentPage,PER_PAGE])

  useEffect(() => {
    console.log('Toy acá 3')
  },[currentPageArr,currentPage])

  function handlePrev() {
    if (currentPage!=0) {
      setCurrentPage(currentPage - 1);
    }
  }

  const handleNext = (e) => {
    if (currentPage < pageCount - 1) {
      setCurrentPage(currentPage + 1);
    }
  }

  function handlePage(e) {
    setCurrentPage(e.target.value - 1);
    console.log('current page is' + currentPage);
  }


  return (
    <div className="p_cards_area">
      {show && <h3> Seleccione primero un orígen para su búsqueda (API/DB)</h3>}
      {gettingG && <h3> ...Obteniendo juegos de la {orig}.</h3>}
      {inits && <>
        <GameCard key={1} id={3498} name={"Grand Theft Auto V"} genres={"Action,Adventure"} />
        <div className="cardcont" >
          {/* <h1>Games to show</h1> */}
          {currentPageArr && currentPageArr.map((g, i) => (
            <GameCard key={i} id={g.apId} name={g.name} img={g.img_url} genres={g.genres} />
          ))}
        </div>
        <div className="p_btn_area">
          <button className="p_button" onClick={handlePrev}>Anterior</button>
          {/* {pages && pages.map((p, i) => (
          <button key={i} className="p_button" onClick={handlePage} value={p}> {p} </button>
        ))} */}
          <button className="p_button" onClick={handleNext}>Siguiente</button>
        </div>
      </>}
    </div>
  )
}
