import React, { useState, useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import './OrigSelect.css';
import { setOrigOpt } from '../../actions/actions';

export default function OriginSelect() {
  let dispatch = useDispatch();

  // Estado que indica que quiere limpiar la búsqueda
  let cleans = useSelector(state => state.cleansearch);

  let origarr = [
    { label: 'Base de datos', value: 'base de datos' }, //DB
    { label: 'API', value: 'API' }
  ];

  let [orig, setOrig] = useState('');

  let handleChange = (e) => {
    setOrig(e.target.value);
  }

  // //Cada vez que cambie el origen guarda el estado en el store
  useEffect(() => {
    // console.log(orig);
    dispatch(setOrigOpt(orig));
  }, [orig, dispatch]);

  // Cuando presione nueva búsqueda que limpie el género
  // useEffect(() => {
  //   if (cleans) {
  //     setOrig('');
  //   }
  // }, [cleans]);

  return (
    <div className="ocont">
      <select
        name="origin"
        className="oselect"
        value={orig}
        onChange={handleChange}
      >
        <option defaultValue >Origen:</option>
        {origarr.map((option, index) => (
          <option key={index} value={option.value}>{option.label}</option>
        ))}
      </select>
    </div>
  )
}
