       img_url: "https://media.rawg.io/media/games/328/3283617cb7d75d67257fc58339188742.jpg",
       genres: "Shooter,Puzzle"
     },
     {
       apId: 3328,
       name: "The Witcher 3: Wild Hunt",
       img_url: "https://media.rawg.io/media/games/618/618c2031a07bbff6b4f611f10b6bcdbc.jpg",
       genres: "Action,Adventure,RPG"
     }
   ],
   dbgames: [
    {
       apId: 3498,
       name: "Grand Theft Auto V",
       img_url: "https://media.rawg.io/media/games/84d/84da2ac3fdfc6507807a1808595afb12.jpg",
       genres: "Action,Adventure"
     },
     {
       apId: 4200,
       name: "Portal 2",
       img_url: "https://media.rawg.io/media/games/328/3283617cb7d75d67257fc58339188742.jpg",
       genres: "Shooter,Puzzle"
     }],
  cleancards: true,
};

function reducer(state = initialState, action) {

  switch (action.type) {
    //------New game------
    case SET_NG_GEN_SEL:
      return {
        ...state,
        nggensel: action.payload
      }
    case SET_NG_PTF_SEL:
      return {
        ...state,
        ngplatfsel: action.payload
      }
    case SEND_FORM:
      return {
        ...state,
        sended: action.payload
      }
    //-----Search game----
    case INIT_SEARCH:
      return {
        ...state,
        initsearch: action.payload
      }
    case CLEAN_SEARCH:
      return {
        ...state,
        cleansearch: action.payload
      }
    case SET_ORIG:
      return {
        ...state,
        searchOrig: action.payload
      }    
    case SHOW_MSG:
      return {
        ...state,
        showmsg: action.payload
      }
    case SET_SEARCH_GEN:
      return {
        ...state,
        searchgenre: action.payload
      }
    case SET_ORDER_OPT:
      return {
        ...state,
        ordering: action.payload
      }
    case DB_GEN_OP_SEARCH:
      return {
        ...state,
        dbgenopts: action.payload
      }
    case API_GEN_OP_SEARCH:
      return {
        ...state,
        apigenopts: action.payload
      }
    case TITLE_SEARCH:
      return {
        ...state,
        stitle: action.payload
      }
    case GET_API_GAMES:
      return {
        ...state,
        apigames: action.payload
      }
    case GET_DB_GAMES:
      return {
        ...state,
        dbgames: action.payload
      }
    case CLEAN_CARDS:
      return {
        ...state,
        cleancards: action.payload
      }

    // case GET_MOVIE_DETAIL:
    //   return {
    //     ...state,
    //     movieDetail: action.payload
    //   }
    // case ADD_MOVIE_FAVORITE:
    //   let foundMovie = state.moviesLoaded.find(peli => peli.imdbID !== action.payload)
