import React, { useState, useEffect, useRef } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import './Pagination.css';
import GameCard from '../GameCard/GameCard.jsx';
import { getApiGames, getdbGames, initsearch } from '../../actions/actions';

export default function Pagination() {

  const dispatch = useDispatch();

  const cleancards = useSelector(state => state.cleancards);
  const origin = useSelector(state => state.searchOrig);
  const resolverRef = useRef(null);

  let [showMsg, setShowMsg] = useState(true);
  let [gettingG, setGettingG] = useState(false);



  // let origRef = useRef(origin);
  useEffect(() => {
    if (origin === 'API') {
      console.log('cargaste API');
      setShowMsg(false);
      setGettingG(true);
    }
    if (origin === 'base de datos') {
      console.log('falta cargar videojuegos en la base de datos');
      setShowMsg(false);
      setGettingG(true);
    }
  }, [origin])


  // useEffect(() => {
  //   console.log(`showMsg: ${showMsg}`);
  // }, [showMsg])


  console.log(`origin:${origin},  showMsg: ${showMsg}, gettingG: ${gettingG}, cleancards: ${cleancards}`)

  return (
    <div>
      {showMsg && <h3> Seleccione primero un orígen para su búsqueda (API/DB)</h3>}
      {gettingG && <h3> ...Obteniendo juegos de la {origin}.</h3>}
      {!cleancards && <>
        <div className="p_cards_area">
          <GameCard />
          <GameCard />
        </div>
        <div className="p_btn_area">
          <button className="p_button">Anterior</button>
          <button className="p_button">Siguiente</button>
        </div>
      </>}
    </div>
  )
}
