import React, { useState, useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import './GenreSelect.css';
import { setSearchGenre } from '../../actions/actions';

//used on SearchMenu

export default function GenreSelect() {

  const dispatch = useDispatch();

  //const sgenres = useSelector(state => state.nggensel);
  async function genresopts(){
    const origen = useSelector(state => state.searchOrig);

  } 

  const genres = [
    { label: 'acción', value: 'acción' },
    { label: 'aventura', value: 'aventura' }
  ];

  let [genre, setGenre] = useState('');

  function handleChange(e) {
    setGenre(e.target.value);
  }

  //Cada vez que cambie el origen guarda el estado en el store
  useEffect(() => {
    dispatch(setSearchGenre(genre));
  }, [genre, dispatch]);

  return (
    <div className="gcont">
      <select
        name="genres"
        className="gselect"
        value={genre}
        onChange={handleChange}
      >
        <option defaultValue >Género:</option>
        {genres && genres.map((option, index) => (
          <option key={index} value={option.value}>{option.label}</option>
        ))}
      </select>
    </div>
  )
}
