import React, { useState, useEffect } from 'react';
import { useSelector } from 'react-redux';
import './GamesToShow.css';
import GameCard from '../GameCard/GameCard.jsx';

export default function GamesToShow() {

  let inits = useSelector(state => state.initsearch);
  let titFiltGames = useSelector(state => state.gamestoshow); // juegos para mostrar
  let orderchoice = useSelector(state => state.ordering);

  let [ordarr, setOrdarr] = useState(null);

  let [pageCount, setPageCount] = useState(null);
  let [currentPage, setCurrentPage] = useState(0); //inicialmente la primer página
  let [currentPageArr, setCurrentPArr] = useState([]);
  let [pages, setPages] = useState(null);
  let PER_PAGE = 3;

  function letsorder(arr, op) {
    if ((op === 'abcCreciente') || (op === 'abcDecreciente')) {
      let ordarr = arr.sort(function (a, b) {
        if (a.name.toLowerCase() > b.name.toLowerCase()) return 1;
        if (a.name.toLowerCase() < b.name.toLowerCase()) return -1;
        return 0;
      });
      if (op === 'abcDecreciente') ordarr = ordarr.reverse();
      return ordarr;
    } else if ((op === 'nroCreciente') || (op === 'nroDecreciente')) {
      let ordarr = arr.sort((a, b) => a.rating - b.rating);
      if (op === 'nroDecreciente') ordarr = ordarr.reverse();
      return ordarr;
    }
  }

  useEffect(() => {
    if (titFiltGames) {
      setOrdarr(titFiltGames)
    }
  }, [titFiltGames])

  //-----------Desde acá cambio titFiltGames por ordarr ------

  useEffect(() => {
    if (titFiltGames) {
      console.log('Toy acá 1')
      let n = titFiltGames.length; // Nro total de juegos a mostrar
      let pc = Math.ceil(n / PER_PAGE); // Nro de páginas a mostrar
      setPageCount(pc);
    }
  }, [titFiltGames, PER_PAGE])

  useEffect(() => {
    if (pageCount) {
      // Función que crea botones para cáda página
      function pagesNumArr(pageCount) {
        let arr = [];
        for (let i = 1; i <= pageCount; i++) {
          arr.push(i);
        }
        return arr;
      }
      let pages = pagesNumArr(pageCount);
      setPages(pages);
    }
  }, [pageCount])

  useEffect(() => {
    if (pages) {
      // Función que obtiene el subarreglo de juegos a mostrar según la pagina
      // en la que se encuentre.
      function inpageshow(currentPage, perpage, titFiltGames) {
        let offset = currentPage * perpage;
        let currentPageArr = titFiltGames.slice(offset, offset + PER_PAGE)
        console.log(currentPageArr);
        setCurrentPArr([...currentPageArr]);
      }

      if (titFiltGames) {
        console.log('Toy acá 2')
        inpageshow(currentPage, PER_PAGE, titFiltGames);
      }
    }
  }, [currentPage, PER_PAGE, titFiltGames, pages])

  function handlePrev() {
    if (currentPage != 0) {
      setCurrentPage(currentPage - 1);
    }
  }

  const handleNext = (e) => {
    console.log(currentPage);
    console.log(pageCount);
    if (currentPage < pageCount - 1) {
      setCurrentPage(currentPage + 1);
    }
  }

  function handlePage(e) {
    setCurrentPage(e.target.value - 1);
    console.log('current page is' + currentPage);
  }

  return (
    <div>
      {inits && <>
        {/* <GameCard key={1} id={3498} name={"Grand Theft Auto V"} genres={"Action,Adventure,Indie, Puzzle,Platformer"} /> */}
        <div className="cardcont" >
          {currentPageArr && currentPageArr.map((g, i) => (//source
            <GameCard key={i} id={g.id} name={g.name} rating={g.rating} img={g.img_url} genres={g.genres} />
          ))}
        </div>
        <div className="p_btn_area">
          <button className="p_button" onClick={handlePrev}>Anterior</button>
          {pages && pages.map((p, i) => (
            <button key={i} className="op_button" onClick={handlePage} value={p}> {p} </button>
          ))}
          <button className="p_button" onClick={handleNext}>Siguiente</button>
        </div>
      </>}
    </div>
  )
}
