import { SET_NG_GEN_SEL, SET_NG_PTF_SEL, SEND_FORM, SEND_SEARCH, SET_ORIG, SET_SEARCH_GEN, SET_ORDER_OPT, DB_GEN_OP_SEARCH,API_GEN_OP_SEARCH } from '../actions/actions';

const initialState = {
  //------New game------
  nggensel: [],
  ngplatfsel: [],
  sended: false,
  //-----Search game----
  searchOps: {},// Opciones de búsqueda de juegos
  searchOrig: 'api',
  searchgenre: '',
  ordering: '',
  dbgenopts: [],
};

function reducer(state = initialState, action) {

  switch (action.type) {
