import {GET_API_PLATFORMS} from '../actions/actions';
//GET_PLATFORMS,GET_GENRES,ADD_NEW_PLATFORM,ADD_NEW_GENRE

const initialState = {
  apiplatforms: [], //arreglo de plataformas de la api
  moviesLoaded: [], // peliculas que nos trae la busqueda
  movieDetail: {} // info de una sola película
};

function reducer(state = initialState, action) {

  switch (action.type) {
    case GET_MOVIES:
      return {
        ...state,
        moviesLoaded: action.payload  //el payload es el arreglo de películas con el nombre buscado
      }
    case GET_MOVIE_DETAIL:
      return {
        ...state,
        movieDetail: action.payload
      }
    case ADD_MOVIE_FAVORITE:
      let foundMovie = state.moviesLoaded.find(peli => peli.imdbID !== action.payload)
      return {
        ...state,
        //moviesFavorite: state.moviesFavorite.concat(foundMovie)
        moviesFavorite: [...state.moviesFavorite,foundMovie]
      }
    case REMOVE_MOVIE_FAVORITE:
      return {
        ...state,
        moviesFavorite: state.moviesFavorite.filter( (peli) => peli.imdbID !== action.payload)
        //crea un nuevo array con todos los elementos que cumplan la condición
        //cada peli es un objeto que en particular tiene una prop id
      }
    default:
      return state;
  }
};

export default reducer;
