import React, { useEffect } from 'react';
import { useSelector } from 'react-redux';
import './GameCard.css';
import star from '../../imgs/star.png';

// {g.id} name={g.name} img={g.img_url} genres={g.genres}
//http://localhost:3000/gamedetail/base%20de%20datos/:id   // DB
//http://localhost:3000/gamedetail/API/:id   va /API/videogames/:id

export default function GameCard({orig, id, name, rating, img, genres }) {
  //console.log(props.game); 

  function handleClick(e) {
    // Funci칩n que me abre una nueva pesta침a con el detalle del juego elegido
    const win = window.open(`/gamedetail/${id}?orig`, "_blank");
    win.focus();
  }

  return (
    <div className="gcardcont" >
      <div className="gcname" onClick={handleClick}>
        <p >{name}</p>
      </div>
      <div className="gcrating">
        <img className="gcstar_img" src={star} alt="star" />
        <p className="gcrating_n"><b className="grt">{rating}</b></p>
      </div>
      {/* <img className="gImg" src={img} alt="game_image" /> */}
      <p className="pgenre"> {genres} </p>
    </div>
  )
}
