import React , { useState }  from 'react';
import './AddNewPlatform.css';
import {connect} from "react-redux";
import { getApiPlatforms } from '../../actions/actions';

export default function AddNewPlatform() {

  let [title, setTitle] = useState('');

  const handleChange = (e) => {
    let newptf = e.target.value;
    setTitle(newptf);
    console.log(title);
  }

  return (
    <input
      id="input_name"
      type="text"
      className="anpforminput"
      value={title}
      name='name'
      placeholder="Ingrese plataforma no existente..."
      onChange={handleChange}>
    </input>
  )
}

// Función que recibe como argumento el estado de Redux y retorna
// como "props" un objeto que contiene el estado.
const mapStateToProps = state => {
  return { plataforms: state.apiplatforms }
}

// Función que recibe como argumento el método dispatch() de Redux
// y retorna un objeto.

const mapDispatchToProps = dispatch => {
	return { getplatforms: () => dispatch(getApiPlatforms()) }
}
