  cleancards: true, // borrar
  gamestoshow: null,
};

function reducer(state = initialState, action) {

  switch (action.type) {
    //------New game------
    case SET_NG_GEN_SEL:
      return {
        ...state,
        nggensel: action.payload
      }
    case SET_NG_PTF_SEL:
      return {
        ...state,
        ngplatfsel: action.payload
      }
    case SEND_FORM:
      return {
        ...state,
        sended: action.payload
      }
    //-----Search game----
    case INIT_SEARCH:
      return {
        ...state,
        initsearch: action.payload
      }
    case CLEAN_SEARCH:
      return {
        ...state,
        cleansearch: action.payload
      }
    case SET_ORIG:
      return {
        ...state,
        searchOrig: action.payload
      }    
    case SHOW_MSG:
      return {
        ...state,
        showmsg: action.payload
      }
    case SET_SEARCH_GEN:
      return {
        ...state,
        searchgenre: action.payload
      }
    case SET_ORDER_OPT:
      return {
        ...state,
        ordering: action.payload
      }
    case DB_GEN_OP_SEARCH:
      return {
        ...state,
        dbgenopts: action.payload
      }
    case API_GEN_OP_SEARCH:
      return {
        ...state,
        apigenopts: action.payload
      }
    case TITLE_SEARCH:
      return {
        ...state,
        stitle: action.payload
      }
    case GET_API_GAMES:
      return {
        ...state,
        apigames: action.payload
      }
    case GET_DB_GAMES:
      return {
        ...state,
        dbgames: action.payload
      }
    case CLEAN_CARDS:
      return {
        ...state,
        cleancards: action.payload
      }
    case SET_GAMES_TO_SHOW:
