import React, { useEffect, useState } from 'react';
import './OrigSelect.css';

export default function OriginSelect() {
  const origarr = [

    { label: 'Base de datos', value: 'db' },
    { label: 'API', value: 'api' }
  ];

  let [orig, setOrig] = useState('Cualquiera');

  function handleChange(e) {
    setOrig(e.target.value);
  }

  return (
    <div  className="ocont">
      <p className="otitle">Or√≠gen:</p>
      <select value={orig} name="origin" className="oselect" onChange={handleChange}>
        {origarr.map((option, index) => (
          <option key={index} value={option.value}>{option.label}</option>
        ))}
      </select>
    </div>
  )
}
