import React, { useState, useEffect, useRef } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import './GenreSelect.css';
import { dbGenOpSearch, apiGenOpSearch, searchGenre } from '../../actions/actions';

//used on SearchMenu

export default function GenreSelect() {

  let dispatch = useDispatch();
  let origin = useSelector(state => state.searchOrig);// <--Busca el origen del store.
  let apigenres = useSelector(state => state.apigenopts);
  let apigenRef = useRef(apigenres);//<--para evitar missing dependency en useEffect
  let dbgenres = useSelector(state => state.dbgenopts);
  let dbgenRef = useRef(dbgenres);
  let cleans = useSelector(state => state.cleansearch);

  let [genreopts, setGenreopts] = useState(''); // opciones de géneros
  let [sgenre, setSgenre] = useState(''); // género seleccionado

  // Cada vez que cambie el origen guarda el estado en el store
  // y setea genreopts con los valores correspondientes
  useEffect(() => {
    let sendgenopsearch = (orig, apig, dbg) => {
      if (orig === 'base de datos') {
        //console.log(orig);
        //dispatch(dbGenOpSearch()); // <-- Hacer...
        setGenreopts(dbg); // <-- Cambiar en store...
      }
      if (orig === 'API') {
        //console.log(orig);
        dispatch(apiGenOpSearch());
        // setGenreopts(apig);
        //console.log(apig);
      }
    }
    sendgenopsearch(origin, apigenRef.current, dbgenRef.current);// <-- referencia a apigenres
  }, [origin, dispatch]);

  //Cada vez que cambie el origen guarda el estado en el store
  useEffect(() => {
    setGenreopts(apigenres);
  }, [apigenres]);

  useEffect(() => {
    setGenreopts(dbgenres);
  }, [dbgenres]);

  // Manda al store el género a buscar
  function handleChange(e) {
    dispatch(searchGenre(e.target.value));
    setSgenre(e.target.value);
  }

  useEffect(() => {
    if (cleans) {
      dispatch(searchGenre(''));
      setSgenre('');
    }
  }, [sgenre]);

  useEffect(() => {
    if (cleans) {
      dispatch(searchGenre(''));
      setSgenre('');
    }
  }, [cleans]);

  return (
    <div className="gcont">
      <select
        name="genres"
        className="gselect"
        value={sgenre}
        onChange={handleChange}
      >
        <option defaultValue  >Género:</option>
        {genreopts && genreopts.map((option, index) => (
          <option key={index} value={option.value}>{option.label}</option>
        ))}
      </select>
    </div>
  )
}
