// Request a la API para traer las plataformas de videojuegos.
export function getApiPlatforms() {
  return function(dispatch) {
    return fetch("https://api.rawg.io/api/platforms") //url de api con plataformas
    .then(response => response.json()) //aplica json y retorna
    .then(json => {
      let data = json.results;
      let apiPlatf = data.map()
      dispatch({ type: GET_MOVIES, payload: json.Search }); // lo manda al reducer
    });
  };
}

