import { React, useState } from 'react';
import './MultiSelectPlatform.css';

export default function MultiSelectGenre() {
  const platforms = [
    { value: 'chocolate', label: 'Chocolate' },
    { value: 'strawberry', label: 'Strawberry' },
    { value: 'vanilla', label: 'Vanilla' }
  ]

  let [platform, setPlatform] = useState([]);

  function handleSelectChange(e) {
    setPlatform(e.target.value);
    alert(``)
  }

  return (
    <div className="selplatfom">
      <select
        name="platforms"
        placeholder="Plataformas..."
        className="multselectpf"
        value={platforms}
        onChange={handleSelectChange}
      >
        <option selected>Plataformas</option>
        {platforms.map((option, index) => (
          <option key={index} value={option.value}>{option.label}</option>
        ))}
      </select>
      <div className="platfcont">
        <button className="selplatfbtn">AcaBoton</button>
      </div>
    </div>
  )
}
