import React, { useState, useEffect, useRef } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import './Pagination.css';
import GameCard from '../GameCard/GameCard.jsx';
import { getApiGames, getdbGames } from '../../actions/actions';

export default function Pagination() {

  const dispatch = useDispatch();

  const origin = useSelector(state => state.searchOrig);
  const apiGames = useSelector(state => state.apigames);
  const dbGames = useSelector(state => state.dbgames);
  const search_gen = useSelector(state => state.searchgenre);
  const search_t = useSelector(state => state.stitle);

  const [showMsg, setShowMsg] = useState(true);
  const [searchingG, setSearchingG] = useState(false);
  const [games, setGames] = useState([]);
  const [genFiltGames, setGenFiltGames] = useState(null);
  const [titFiltGames, setTitFiltGames] = useState(null);

  // Según el origen busca el arreglo de juegos
  useEffect(() => {
    if (origin === 'api') {
      // dispatch(getApiGames());
      setShowMsg(false);
      setSearchingG(true);
    }
    if (origin === 'db') {
      console.log('falta cargar videojuegos en la base de datos');
      // dispatch(getdbGames());
      setShowMsg(false);
      setSearchingG(true);
    }
  }, [origin, dispatch])

  useEffect(() => {
  }, [showMsg, searchingG])

  let origRef = useRef(origin)
  // Una vez cargados los juegos en el store desactiva mje "buscando"
  useEffect(() => {
    setSearchingG(false);
    if(origRef.current === 'api'){
      setGames(apiGames)
    }
    if(origRef.current === 'db'){
      setGames(dbGames)
    }
  }, [apiGames, dbGames])

  function genreFilter(arr, str) {
    let newarr = arr.filter(e => e.genres.includes(str));
    return newarr;
  }
  // Si se especifica un género para filtar llama a genreFilter
  useEffect(() => {
    let newarr = genreFilter(games, search_gen);
    setGenFiltGames(newarr);
  }, [search_gen])

  function titleFilter(arr, str) {
    let newarr = arr.filter(e => e.name.includes(str));
    setTitFiltGames(newarr);
    return newarr;
  }

  useEffect(() => {
    let newarr = titleFilter(genFiltGames||games, search_t );// Si no filtro por género que deje los de la api
    setTitFiltGames(newarr);
  }, [search_gen])

  // traerme el arreglo
  // traerme el género y el título a buscar
  // hacer el filtrado
  // mostrar de a 6
  // 

  // si es api y el texto de búsqueda es '' trae 100 de la ruta local / videogames


  return (
    <div>
      {showMsg && <h3> Seleccione primero el orígen de su búsqueda (API/DB)</h3>}
      {searchingG && <h3> ...Obteniendo juegos de origen</h3>}
      <div className="p_cards_area">
        {/* <GameCard />
        <GameCard /> */}
      </div>
      <div className="p_btn_area">
        <button className="p_button">Anterior</button>
        <button className="p_button">Siguiente</button>
      </div>
    </div>
  )
}
