import React from 'react';
import { useParams } from "react-router";
import { useLocation } from "react-router-dom";
import './GameDetail.css';
import './GDInfo.css';
import Navbar from '../Navbar/Navbar';
import star from '../../imgs/star.png';

export default function GameDetail() {

  let { id } = useParams(); // <-- Retorna un objeto, acá es {id: Nro}

  let urlQuery = new URLSearchParams(useLocation().search);  //<-- Retorna un objeto
  let orig = urlQuery.get('orig'); //obtiene el query cuya key es 'orig'.

  async function getGameinfo(id, orig) {
    if (orig === 'API') {
      let objg = await fetch(`http://localhost:3001/videogame/${id}`)
        .then(response => response.json())
        .then(obj => {
          let { id, name, description, released, img_url, rating, platforms, genres } = obj;
          let genrenames = genres.map(e => e.name);
          let str = `${genrenames.toString().replace(/,/g, ', ')}`;
          return { id, name, description, released, img_url, rating, platforms, genres: str }
        })
      return objg;

    } else if (orig === 'base%20de%20datos') {
      let objg = await fetch(`http://localhost:3001/dbvideogame/${id}`)
        .then(response => response.json())
        .then(obj => {
          let { id, name, description, released, img_url, rating, platforms, genres } = obj;
          let genrenames = genres.map(e => e.name);
          let str = `${genrenames.toString().replace(/,/g, ' - ')}`;
          return { id, name, description, released, img_url, rating, platforms, genres: str }
        })
        return objg;
    }

  }

  let { name, description, released, img_url, rating, platforms, genres } =  getGameinfo(id, orig);


  return (
    <div className="ng_page">
      <Navbar />
      <div className="gd_area">
        <h1 className="gd_title"  >Nombre del juego </h1>
        <div className="gd_picture" ></div>
        <div className="gd_picture-info">
          <div className="star">
            <img className="star_img" src={star} alt="star" />
            <p className="gdpT"><b className="gdt">{rating}</b></p>
          </div>
          <p className="gdpT"><b className="gdt">Fecha de lanzamiento:</b>{released}</p>
          <p className="gdpT"><b className="gdt">Género:</b>{genres} </p>
          <p className="gdpT"><b className="gdt">Plataformas:</b>{platforms} </p>
          <p className="gdpT"><b className="gdt">Descripción:</b>{description}</p>
          <div>
          </div>
        </div>
      </div>
    </div>
  )
}
