import React, { useState , useEffect,useRef } from 'react';
import { useDispatch , useSelector} from 'react-redux';
import './SearchBar.css';
import { titleSearch } from '../../actions/actions';

export default function SearchBar() {

  const dispatch = useDispatch();
  const inits = useSelector(state => state.initsearch);
  const cleans = useSelector(state => state.cleansearch);
  const [gname,setGname] = useState('');
  const [cleaninp,setCleaninp] = useState(false);

  // Registra cambios en el input de búsqueda 
  // y lo guarda en el estado local del componente
  const handleChange = (e) => {
    console.log(e.target.value);
    setGname(e.target.value);
  }

  // Cada vez que presione buscar, manda el titulo al store y limpia el input
  let gref = useRef(gname);
  useEffect(() => {
    if(inits){
      console.log(`buscaste ${gname}`);
      dispatch(titleSearch(gname));
    }
  },[inits,gname,dispatch]);

  useEffect(() => {
    if(cleans){
      setGname('');
      console.log(`buscaste ${gname}`);
      dispatch(titleSearch(''));
    }
  },[cleans,gname,dispatch]);

  console.log(inits,gname);

  return (
    <>
      <input className="search_input"
        type="text" 
        placeholder=" Ingrese el nombre del juego..." 
        value = {gname}
        onChange = {handleChange}
      />
    </>
  )
};
