export const GET_API_PLATFORMS = "GET_API_PLATFORMS";
export const SET_GENSET_NG_GEN_SELRE_OPT = "SET_GENRE_OPT";
export const SET_NG_GEN_SEL = "SET_NG_GEN_SEL";

