export const GET_MOVIES = "GET_MOVIES";
export const GET_MOVIE_DETAIL = "GET_MOVIE_DETAIL";
export const ADD_MOVIE_FAVORITE = "ADD_MOVIE_FAVORITE";
export const REMOVE_MOVIE_FAVORITE = "REMOVE_MOVIE_FAVORITE";

// Request a la API para traer las películas.
// El payload va a ser el objeto que recibamos de nuestra request.
export function getMovies(titulo) {
  return function(dispatch) {
    return fetch("http://www.omdbapi.com/?apikey=20dac387&s=" + titulo) //url para pedir por tìtulo
    .then(response => response.json()) //aplica json y retorna
    .then(json => {
      dispatch({ type: GET_MOVIES, payload: json.Search }); // lo manda al reducer
    });
  };
}
